import re

# Idea stolen from https://www.reddit.com/r/adventofcode/comments/3xflz8/day_19_solutions/
m = ""
el_pattern = re.compile("([A-Z][a-z]*)")

elements = el_pattern.findall(m)
outside = 0
y = 0

for el in elements:
    if el in ["Ar", "Rn"]:
        outside += 1
    elif el == 'Y':
        y += 1

print(len(elements) - outside - 2*y - 1)
